mapper.select:
select dongcode, sidoName, gugunName, dongName
from dongcode
where dongCode=#{dong}

select *
from houseinfo i inner join housedeal d
where i.aptCode = d.aptCode
and i.dongCode = #{dong}
and d.dealYear = #{year}
and d.dealMonth = #{month}
and i.apartmentName like "%${name}%"
order by ${order}
limit #{start}, #{pg}

select count(*)
from houseinfo i inner join housedeal d
where i.aptCode = d.aptCode
and i.dongCode = #{dong}
and d.dealYear = #{year}
and d.dealMonth = #{month}
and i.apartmentName like "%${name}%"
